package de.beyondjava.dominio.dao;import org.springframework.data.domain.*;import org.springframework.data.jpa.repository.JpaRepository;import de.beyondjava.dominio.modelo.Localidad;import de.beyondjava.dominio.modelo.Provincia;import java.util.List;public interface LocalidadRepository extends JpaRepository<Localidad, Long>, LocalidadDao {	Page<Localidad> findAll(Pageable pageable);	Localidad findByNombreAllIgnoringCase(String nombre);	List<Localidad> findByProvincia(Provincia provincia);	List<Localidad> findByProvinciaId(Long id);}